try {
  // Get both outputs
  const backupOutputRaw = $input.item.json.output;
  const primaryOutputRaw = $('Check Agent 1 Confidence').first().json.output;

  // Parse function
  function safeParse(data) {
    if (!data) return null;
    if (typeof data === 'object') return data;
    if (typeof data === 'string') {
      try {
        let cleaned = data.trim()
          .replace(/^```json\s*/i, '')
          .replace(/^```\s*/i, '')
          .replace(/\s*```$/i, '');
        return JSON.parse(cleaned);
      } catch (e) {
        return null;
      }
    }
    return null;
  }

  const primary = safeParse(primaryOutputRaw);
  const backup = safeParse(backupOutputRaw);

  if (!primary || !backup) {
    return {
      json: {
        error: "Failed to parse",
        primaryParsed: !!primary,
        backupParsed: !!backup
      }
    };
  }

  // Average scores
  const avgFactScore = Math.round(
    ((primary.fact_accuracy_score || 50) + (backup.fact_accuracy_score || 50)) / 2
  );

  const avgDeceptScore = Math.round(
    ((primary.deceptiveness_score || 50) + (backup.deceptiveness_score || 50)) / 2
  );

  // Determine classification
  let finalClassification;
  const avgIntent = primary.appears_intentional || backup.appears_intentional;

  if (avgFactScore < 40) {
    finalClassification = avgIntent ? 'DISINFORMATION' : 'MISINFORMATION';
  } else if (avgFactScore >= 60 && avgDeceptScore > 60) {
    finalClassification = 'PROPAGANDA';
  } else if (avgFactScore > 80 && avgDeceptScore < 30) {
    finalClassification = 'LEGITIMATE';
  } else {
    finalClassification = 'BIASED_BUT_FACTUAL';
  }

  // Calculate confidence
  const factDiff = Math.abs((primary.fact_accuracy_score || 50) - (backup.fact_accuracy_score || 50));
  const deceptDiff = Math.abs((primary.deceptiveness_score || 50) - (backup.deceptiveness_score || 50));
  const avgDiff = (factDiff + deceptDiff) / 2;

  const finalConfidence = avgDiff <= 10 ? 'high' : avgDiff <= 20 ? 'medium' : 'low';

  // Recommendation
  let recommendation;
  if (finalClassification === 'PROPAGANDA' || finalClassification === 'DISINFORMATION') {
    recommendation = 'FLAG_AS_FALSE_NEWS';
  } else if (finalClassification === 'MISINFORMATION' || finalClassification === 'BIASED_BUT_FACTUAL') {
    recommendation = 'LABEL_AS_BIASED';
  } else {
    recommendation = 'NO_ACTION_NEEDED';
  }

  // Merge arrays
  const mergedPatterns = [...new Set([
    ...(primary.false_news_patterns_detected || []),
    ...(backup.false_news_patterns_detected || [])
  ])];

  const mergedOmissions = [...new Set([
    ...(primary.key_omissions || []),
    ...(backup.key_omissions || [])
  ])];

  const mergedTechniques = [...new Set([
    ...(primary.manipulation_techniques || []),
    ...(backup.manipulation_techniques || [])
  ])];

  // Return merged result
  return {
    json: {
      output: JSON.stringify({
        classification: finalClassification,
        fact_accuracy_score: avgFactScore,
        deceptiveness_score: avgDeceptScore,
        appears_intentional: avgIntent,
        confidence: finalConfidence,
        verified_claims: backup.verified_claims || primary.verified_claims || [],
        false_news_patterns_detected: mergedPatterns,
        key_omissions: mergedOmissions,
        manipulation_techniques: mergedTechniques,
        overall_assessment: `Primary: ${primary.overall_assessment || 'N/A'}. Backup: ${backup.overall_assessment || 'N/A'}. Agreement: ${avgDiff <= 10 ? 'Strong' : 'Moderate'} (diff: ${Math.round(avgDiff)})`,
        recommendation: recommendation,
        dual_verification: true,
        agent_comparison: {
          fact_score_diff: factDiff,
          deceptiveness_diff: deceptDiff,
          avg_difference: Math.round(avgDiff),
          primary_classification: primary.classification,
          backup_classification: backup.classification
        }
      })
    }
  };

} catch (error) {
  return {
    json: {
      error: error.message,
      stack: error.stack
    }
  };
}

try {
  // Get both outputs (using same method as Agent 1 merge)
  const backupOutputRaw = $input.item.json.output;
  const primaryOutputRaw = $('Check Agent 2 Confidence').first().json.output;

  // Parse function
  function safeParse(data) {
    if (!data) return null;
    if (typeof data === 'object') return data;
    if (typeof data === 'string') {
      try {
        let cleaned = data.trim()
          .replace(/^```json\s*/i, '')
          .replace(/^```\s*/i, '')
          .replace(/\s*```$/i, '');
        return JSON.parse(cleaned);
      } catch (e) {
        return null;
      }
    }
    return null;
  }

  const primary = safeParse(primaryOutputRaw);
  const backup = safeParse(backupOutputRaw);

  if (!primary || !backup) {
    return {
      json: {
        error: "Failed to parse",
        primaryParsed: !!primary,
        backupParsed: !!backup
      }
    };
  }

  // Average trustworthiness score
  const avgTrustScore = Math.round(
    ((primary.overall_trustworthiness_score || 50) + (backup.overall_trustworthiness_score || 50)) / 2
  );

  // Determine final rating
  let finalRating;
  if (avgTrustScore >= 80) finalRating = 'HIGH';
  else if (avgTrustScore >= 60) finalRating = 'MEDIUM';
  else if (avgTrustScore >= 40) finalRating = 'LOW';
  else finalRating = 'VERY_LOW';

  const scoreDiff = Math.abs(
    (primary.overall_trustworthiness_score || 50) - (backup.overall_trustworthiness_score || 50)
  );

  // Merge sources and red flags
  const mergedSourcesChecked = [...new Set([
    ...(primary.source_credibility?.sources_checked || []),
    ...(backup.source_credibility?.sources_checked || [])
  ])];

  const mergedRedFlags = [...new Set([
    ...(primary.source_credibility?.red_flags || []),
    ...(backup.source_credibility?.red_flags || [])
  ])];

  // Return merged result
  return {
    json: {
      output: JSON.stringify({
        source_credibility: {
          rating: finalRating,
          score: avgTrustScore,
          source_identified: primary.source_credibility?.source_identified || backup.source_credibility?.source_identified || 'unknown',
          source_type: primary.source_credibility?.source_type || backup.source_credibility?.source_type || 'unknown',
          explanation: `Primary: ${primary.source_credibility?.explanation || 'N/A'}. Backup: ${backup.source_credibility?.explanation || 'N/A'}`,
          sources_checked: mergedSourcesChecked,
          red_flags: mergedRedFlags,
          extraction_method: primary.source_credibility?.extraction_method || backup.source_credibility?.extraction_method || 'none'
        },
        bot_likelihood: primary.bot_likelihood || backup.bot_likelihood || {
          assessment: 'NOT_APPLICABLE',
          data_available: false
        },
        overall_trustworthiness_score: avgTrustScore,
        recommendation: `Average trustworthiness: ${avgTrustScore}/100. Agreement: ${scoreDiff === 0 ? 'Perfect' : scoreDiff <= 15 ? 'Strong' : 'Moderate'}`,
        scenario: primary.scenario || backup.scenario || 'unknown',
        dual_verification: true,
        agent_comparison: {
          score_difference: scoreDiff,
          primary_score: primary.overall_trustworthiness_score,
          backup_score: backup.overall_trustworthiness_score,
          primary_rating: primary.source_credibility?.rating,
          backup_rating: backup.source_credibility?.rating
        }
      })
    }
  };

} catch (error) {
  return {
    json: {
      error: error.message,
      stack: error.stack
    }
  };
}

// Format Agent 4 output for Google Sheets
const agent4Output = $input.first().json.output;
const originalData = $('Parse Input Data').first().json;

// Parse Agent 4 JSON output
let assessment = {};
try {
  if (typeof agent4Output === 'string') {
    const cleaned = agent4Output.trim()
      .replace(/^```json\s*/i, '')
      .replace(/^```\s*/i, '')
      .replace(/\s*```$/i, '');
    assessment = JSON.parse(cleaned);
  } else {
    assessment = agent4Output;
  }
} catch (e) {
  assessment = { error: 'Failed to parse Agent 4 output' };
}

// Determine source type
const sourceType = originalData.sourceType || 'unknown';
const source = sourceType === 'twitter' ? 'Twitter' : 
               sourceType === 'dataset' ? 'WhatsApp/Dataset' : 
               'Unknown';

// Get original content
const content = originalData.tweetText || originalData.text || 'N/A';
const contentPreview = content.substring(0, 200);

// Extract scores
const riskLevel = assessment.final_assessment?.risk_level || 'UNKNOWN';
const compositeScore = assessment.final_assessment?.composite_score || 0;
const confidence = assessment.final_assessment?.confidence || 'UNKNOWN';

// Extract contributing factors
const factCheckClass = assessment.contributing_factors?.fact_check_classification || 'N/A';
const factCheckScore = assessment.contributing_factors?.fact_check_score || 0;
const sourceCredibility = assessment.contributing_factors?.source_credibility || 'N/A';
const sourceScore = assessment.contributing_factors?.source_score || 0;
const accountAuth = assessment.contributing_factors?.account_authenticity || 'N/A';
const accountScore = assessment.contributing_factors?.account_score || 0;

// Extract concerns and actions
const keyConcerns = (assessment.key_concerns || []).join('; ');
const recommendedAction = assessment.recommended_action?.primary_action || 'N/A';
const urgency = assessment.recommended_action?.urgency || 'N/A';
const rationale = assessment.recommended_action?.rationale || 'N/A';
const summary = assessment.summary || 'N/A';

// Get metadata
const tweetUrl = originalData.tweetMetadata?.tweet_url || originalData.tweet_url || 'N/A';
const author = originalData.accountData?.username || 'N/A';
const dataset = originalData.dataset || 'N/A';
const supabaseId = originalData.supabase_id || 'N/A';

return {
  json: {
    timestamp: new Date().toISOString(),
    source: source,
    source_type: sourceType,
    content_preview: contentPreview,
    full_content: content,

    // Risk Assessment
    risk_level: riskLevel,
    composite_score: compositeScore,
    confidence: confidence,

    // Fact Check
    fact_check_classification: factCheckClass,
    fact_check_score: factCheckScore,

    // Source Credibility
    source_credibility_rating: sourceCredibility,
    source_credibility_score: sourceScore,

    // Account Analysis
    account_authenticity: accountAuth,
    account_score: accountScore,

    // Actions & Concerns
    key_concerns: keyConcerns,
    recommended_action: recommendedAction,
    urgency: urgency,
    rationale: rationale,
    summary: summary,

    // Metadata
    tweet_url: tweetUrl,
    author: author,
    dataset: dataset,
    supabase_id: supabaseId,

    // Raw output for reference
    raw_assessment: JSON.stringify(assessment)
  }
};

