// Combine all 3 agent outputs into one structured object
const allInputs = $input.all();

// Safe parse function
function safeParse(data) {
  if (!data) return null;
  if (typeof data === 'object' && !Array.isArray(data)) return data;
  if (typeof data === 'string') {
    try {
      let cleaned = data.trim()
        .replace(/^```json\s*/i, '')
        .replace(/^```\s*/i, '')
        .replace(/\s*```$/i, '');
      return JSON.parse(cleaned);
    } catch (e) {
      console.error('Parse error:', e);
      return null;
    }
  }
  return null;
}

// Parse each input
const factCheck = safeParse(allInputs[0]?.json?.output);
const sourceCheck = safeParse(allInputs[1]?.json?.output);
const accountCheck = safeParse(allInputs[2]?.json?.output);

// Return combined data
return {
  json: {
    factCheck: factCheck || {
      classification: 'UNVERIFIABLE',
      fact_accuracy_score: 50,
      deceptiveness_score: 50,
      appears_intentional: false,
      confidence: 'low'
    },
    sourceCheck: sourceCheck || {
      source_credibility: { rating: 'UNKNOWN', score: 50 },
      overall_trustworthiness_score: 50
    },
    accountCheck: accountCheck || {
      bot_probability: 'NOT_APPLICABLE',
      authenticity_score: 50,
      data_available: false
    }
  }
};

// Pass through API response but add itemLimit from previous node
const itemLimit = $('Set Limit (Scheduled = 10)').first()?.json?.itemLimit || $('Set Limit (Manual = 5)').first()?.json?.itemLimit || 10;

const apiResponse = $input.first().json;

return {
  json: {
    ...apiResponse,
    itemLimit: itemLimit
  }
};

// Get the item limit from input (passed from previous node)
const itemLimit = $input.first().json.itemLimit || 10;
console.log('🎯 Using itemLimit:', itemLimit);

const response = $input.first().json;

// Debug: Log the response structure
console.log('📊 API Response keys:', Object.keys(response));
console.log('📊 Full response sample:', JSON.stringify(response).substring(0, 500));

// Try multiple possible response formats
const tweets = response.timeline || response.tweets || response.results || response.data || [];

if (!Array.isArray(tweets) || tweets.length === 0) {
  console.log('❌ No tweets array found. Response:', response);
  return [{json: {error: 'No tweets found', raw: response, keys: Object.keys(response)}}];
}

console.log('✅ Found', tweets.length, 'tweets');

// Calculate virality for each tweet with flexible field mapping
const ranked = tweets.map(t => {
  // Try multiple field name variations
  const retweets = t.retweet_count || t.retweetCount || t.retweets || t.public_metrics?.retweet_count || 0;
  const likes = t.favorite_count || t.favoriteCount || t.likeCount || t.likes || t.public_metrics?.like_count || 0;
  const replies = t.reply_count || t.replyCount || t.replies || t.public_metrics?.reply_count || 0;
  const quotes = t.quote_count || t.quoteCount || t.quotes || t.public_metrics?.quote_count || 0;

  const viralityScore = (retweets * 2) + likes + (replies * 3) + (quotes * 2);

  return {
    ...t,
    virality_score: viralityScore,
    _retweets: retweets,
    _likes: likes,
    _replies: replies,
    _quotes: quotes
  };
});

// Sort by virality (highest first)
ranked.sort((a, b) => b.virality_score - a.virality_score);

// Take top N (based on itemLimit)
const topN = ranked.slice(0, itemLimit);

console.log('🏆 Top', topN.length, 'tweets by virality:', topN.map(t => t.virality_score));

return topN.map((t, i) => {
  // Try multiple user field variations
  const user = t.user || t.author || t.author_data || {};

  // Try multiple ID field variations
  const id = t.id_str || t.id || t.tweet_id || t.tweetId;

  // Try multiple text field variations
  const text = t.full_text || t.text || t.tweet_text || t.content || '';

  // Try multiple username variations
  const username = user.screen_name || user.username || user.userName || user.name || t.screen_name || t.username || 'unknown';

  return {
    json: {
      rank: i + 1,
      tweet_id: id,
      tweet_text: text,
      tweet_url: id ? `https://twitter.com/i/web/status/${id}` : 'N/A',
      virality_score: t.virality_score,
      engagement: `${t._retweets} RT | ${t._likes} ♥ | ${t._replies} 💬`,
      author: `@${username}`,
      verified: (user.verified || user.isVerified || t.verified) ? '✓' : '✗',
      preview: text.substring(0, 150) + '...',

      // For your misinformation pipeline - BASIC DATA ONLY
      tweetText: text,
      tweetSource: 'twitter_viral',
      sourceType: 'twitter',
      accountData: {
        username: username,
        verified: user.verified || user.isVerified || t.verified || false,
        followers: user.followersCount || user.followers_count || user.followers || t.followers_count || 0
      },

      // Debug info
      _debug: {
        original_keys: Object.keys(t).slice(0, 10),
        user_keys: Object.keys(user).slice(0, 10)
      }
    }
  };
});
