// Average scores from primary and backup agents
const primary = $input.first().json;
const backup = $input.last().json;

const averagedScore = Math.round((primary.fact_check_score + backup.fact_check_score) / 2);

const result = {
  verdict: primary.confidence === 'high' ? primary.verdict : (backup.confidence === 'high' ? backup.verdict : primary.verdict),
  confidence: (primary.confidence === 'low' && backup.confidence === 'low') ? 'low' : 'medium',
  fact_check_score: averagedScore,
  verified_claims: primary.verified_claims,
  overall_assessment: `Primary: ${primary.overall_assessment}. Backup: ${backup.overall_assessment}`,
  backup_used: true
};

return result;

