// Average scores from primary and backup agents
// This node is triggered by Agent 1B (backup)
// Get primary agent output from the original Agent 1 node
const primaryOutput = $('Agent 1 - Fact Check').item.json.output;
const backupOutput = $input.item.json.output;

const primary = JSON.parse(primaryOutput || '{}');
const backup = JSON.parse(backupOutput || '{}');

const averagedScore = Math.round(((primary.fact_check_score || 0) + (backup.fact_check_score || 0)) / 2);

const result = {
  output: JSON.stringify({
    verdict: primary.confidence === 'high' ? primary.verdict : (backup.confidence === 'high' ? backup.verdict : primary.verdict),
    confidence: (primary.confidence === 'low' && backup.confidence === 'low') ? 'low' : 'medium',
    fact_check_score: averagedScore,
    verified_claims: primary.verified_claims || [],
    overall_assessment: `Primary: ${primary.overall_assessment}. Backup: ${backup.overall_assessment}`,
    backup_used: true
  })
};

return {json: result};

// Average scores from primary and backup agents
