const primaryOutput = $('Agent 2 - Credibility').item.json.output;
const backupOutput = $input.item.json.output;

const primary = JSON.parse(primaryOutput || '{}');
const backup = JSON.parse(backupOutput || '{}');

const averagedScore = Math.round(((primary.overall_trustworthiness_score || 0) + (backup.overall_trustworthiness_score || 0)) / 2);

const result = {
  output: JSON.stringify({
    source_credibility: primary.source_credibility,
    bot_likelihood: primary.bot_likelihood,
    overall_trustworthiness_score: averagedScore,
    recommendation: `Primary: ${primary.recommendation}. Backup: ${backup.recommendation}`,
    backup_used: true
  })
};

return {json: result};

const item = $input.item;
const body = item.json.messages?.[0]?.text?.body || 'F600';

// Extract dataset type (T or F) and index
const datasetType = body.charAt(0).toUpperCase();
const idx = parseInt(body.substring(1)) || 600;

return {
  json: {
    datasetType: datasetType,
    idx: idx,
    dataset: datasetType === 'T' ? 'true-news' : 'false-news'
  },
};

// Format data for Parse Input Data node
const item = $input.item.json;

// Check if this is Dataset data or Twitter data
const isDataset = item.dataset || item.text || item.tweetText;
const isTwitter = item.body;

if (isDataset) {
  // Dataset flow - data already formatted by AI Agent1
  return {
    json: {
      tweetText: item.text || '',
      tweetSource: item.tweetSource || item.dataset || 'N/A',
      tweetMetadata: item.tweetMetadata || {},
      accountData: item.accountData || {},
      sourceType: item.sourceType || 'dataset',
      supabase_id: item.supabase_id,
      dataset: item.dataset,
      datasetType: item.datasetType,
      title: item.title,
      subject: item.subject,
      date: item.date
    }
  };
} else if (isTwitter) {
  // Twitter flow - pass through with proper structure
  return {
    json: {
      tweetText: item.body.tweetText || '',
      tweetSource: item.body.tweetSource || 'N/A',
      tweetMetadata: item.body.tweetMetadata || {},
      accountData: item.body.accountData || {},
      sourceType: 'twitter'
    }
  };
} else {
  // Fallback - treat as text input
  return {
    json: {
      tweetText: JSON.stringify(item),
      tweetSource: 'N/A',
      tweetMetadata: {},
      accountData: {},
      sourceType: 'manual'
    }
  };
}
